function getCookie(e){var t=document.cookie.split(";");for(var n=0;n<t.length;n++){var r=t[n].trim();if(r.indexOf(e+"=")==0)return r.substring(e+"=".length,r.length).split("=",2)[1]}}cWidth=window.outerWidth;sWidth=getCookie("sWidth");if(sWidth!=cWidth&&navigator.cookieEnabled){document.cookie="cWidth="+cWidth+"; path=/";window.location.reload()}window.onresize=function(){if(this.resizeTO)clearTimeout(this.resizeTO);this.resizeTO=setTimeout(function(){cWidth=window.outerWidth;sWidth=getCookie("sWidth");if(sWidth!=cWidth&&navigator.cookieEnabled){document.cookie="cWidth="+cWidth+"; path=/";window.location.reload()}},500)}